mapper.update:
INSERT INTO T_RELEASEINFO (BOARD_UID, NUMBER, TITLE, CONTENTS, REL_DT, RSGSTR_ID, VISIT_CNT, RSGST_DT, FILE_NO, USE_YN) VALUES (#{boardUid}
, (SELECT IFNULL(MAX(NUMBER),0)+1 FROM T_RELEASEINFO AS TC)
,#{number}
, #{title}, #{contents},#{relDt}, "SAMPLE", "0", now()
,(SELECT IFNULL(MAX(FILE_NO),0)+1 FROM T_FILE AS TR)
,#{fileNo}
,"Y") ON DUPLICATE KEY UPDATE TITLE = #{title}, CONTENTS = #{contents}, FILE_NO = (SELECT IFNULL(MAX(FILE_NO),0)+1 FROM T_FILE AS TR)

UPDATE T_RELEASEINFO
SET USE_YN = IF(USE_YN = 'Y','N','Y')
WHERE NUMBER = #{number}

UPDATE T_REVIEW
SET USE_YN = IF(USE_YN = 'Y','N','Y')
WHERE NUMBER = #{number}

UPDATE T_NOTICE
SET USE_YN = IF(USE_YN = 'Y','N','Y')
WHERE NUMBER = #{number}

mapper.insert:
        INSERT INTO T_FILE
