            RSGST_DT,
            RSGSTR_ID
        )
        VALUES
        (	

				(SELECT IFNULL(MAX(FILE_NO),0)+1 FROM T_FILE AS TF)


				,#{fileNo}

            ,#{boardUid}
            ,#{originFileNm}
            ,#{savedFileNm}
            ,#{savedFilePath}
            ,#{fileSize}
            ,NOW()
            ,'SAMPLE'
        )
