mapper.update:
INSERT INTO T_RELEASEINFO (BOARD_UID, NUMBER, TITLE, CONTENTS, REL_DT,USER_ID, VISIT_CNT, RSGST_DT, FILE_NO, USE_YN)
VALUES 		(#{boardUid}

	, (SELECT IFNULL(MAX(NUMBER),0)+1 FROM T_RELEASEINFO AS TC)


	,#{number}

, #{title}, #{contents},#{relDt}, "SAMPLE", "0", now(), (SELECT IFNULL(MAX(FILE_NO),0)+1 FROM T_RELEASEINFO AS TC),"Y")
ON DUPLICATE KEY 
UPDATE TITLE 	= #{title},
	   CONTENTS = #{contents}

UPDATE T_RELEASEINFO
SET USE_YN = 'N'
WHERE NUMBER = #{number}


UPDATE T_RELEASEINFO
SET VISIT_CNT = VISIT_CNT +1
WHERE NUMBER = #{number}
