mapper.update:
INSERT INTO T_REVIEW_RPL ( RPL_NO, NUMBER, CAT_CD, GD_CD, PAST_LENGTH, NEW_LENGTH, SIZE, RSGSTR_ID, RSGST_DT)
VALUES 		((SELECT IFNULL(MAX(RPL_NO),0)+1 FROM T_REVIEW_RPL AS TV)
			, #{number}
			, #{catCd}
			, #{gdCd}
			, #{pastLength}
			, #{newLength}
			, #{size}
			, #{rsgstrId}
			, NOW())

UPDATE T_REVIEW
SET USE_YN = 'N'
WHERE NUMBER = #{number}

INSERT INTO T_REVIEW (BOARD_UID, NUMBER, CAT_CD, TITLE, CONTENTS, RSGSTR_ID, VISIT_CNT, RSGST_DT, USE_YN,FILE_NO)
VALUES 		(#{boardUid}

	, (SELECT IFNULL(MAX(NUMBER),0)+1 FROM T_REVIEW AS TV)


	,#{number}

, #{catCd}, #{title}, #{contents}, "SAMPLE", "0", now(), 'Y'

	,(SELECT IFNULL(MAX(FILE_NO),0)+1 FROM T_FILE AS TR))


	,#{fileNo})

ON DUPLICATE KEY 
UPDATE TITLE 	= #{title},
	   CONTENTS = #{contents}

mapper.insert:
        INSERT INTO T_FILE
        (
            FILE_NO,
            BOARD_UID,
            ORIGIN_FILE_NM,
            SAVED_FILE_NM,
            SAVED_FILE_PATH,
            FILE_SIZE,
            RSGST_DT,
            RSGSTR_ID
        )
        VALUES
        (	

				(SELECT IFNULL(MAX(FILE_NO),0)+1 FROM T_FILE AS TF)


				,#{fileNo}

            ,#{boardUid}
            ,#{originFileNm}
            ,#{savedFileNm}
            ,#{savedFilePath}
            ,#{fileSize}
            ,NOW()
            ,'SAMPLE'
        )

mapper.select:
SELECT GD_CD, GD_NM FROM T_PRD
AND CAT_CD = #{value}
ORDER BY GD_CD ASC
